// Función para cargar los datos en el navegador y limpiar versiones viejas
function inicializarDatos() {
    const versionActual = "v_490_productos1"; // Cambiamos la versión para forzar actualización
    
    if (localStorage.getItem("version_db") !== versionActual) {
        console.log("Detectada nueva versión del catálogo. Actualizando...");
        localStorage.clear(); // Borramos datos antiguos
        localStorage.setItem("perfumes_db", JSON.stringify(productosBase));
        localStorage.setItem("version_db", versionActual);
        
        // Recargamos la página para que el usuario vea los cambios inmediatamente
        location.reload(); 
    }
}

// --- Funciones Estándar ---
function obtenerProductos() {
    const lista = localStorage.getItem("perfumes_db");
    return lista ? JSON.parse(lista) : [];
}

function guardarProducto(nuevoProducto) {
    const productos = obtenerProductos();
    productos.push(nuevoProducto);
    localStorage.setItem("perfumes_db", JSON.stringify(productos));
}

function eliminarProductoDB(id) {
    let productos = obtenerProductos();
    productos = productos.filter(producto => producto.id !== id);
    localStorage.setItem("perfumes_db", JSON.stringify(productos));
}

// Ejecutar la inicialización
inicializarDatos();